import { Product } from "../models/product.model";
export function productsAPIResponseMapper(response) {
  const products = [];
  response.forEach((product, index) => {
    products.push(new Product(product.name, product.price, product.currency, product.imageURL, product.id));
  });
  return products;
}
export function SingleProductResponseMapper(response) {
  return new Product(response.name, response.price, response.currency, response.imageURL, response.id);
}
